<?php	session_start();	header('Cache-Control: no-cache, must-revalidate');	header('Expires: Mon, 26 Jul 1997 05:00:00 GMT');	header('Content-type: application/json');	require_once "../model/Gallerie.php";	require_once "../model/Image.php";	require_once "../model/User.php";	$reponse = new stdClass();	$reponse->success = false;	$reponse->message = "";	$reponse->titre = "";	$reponse->description = "";	$reponse->auteur = "";	$reponse->theme = "";	$reponse->confid = "";	$reponse->jaime = "";	$reponse->lien = "";	if (isset($_POST['id']) && !empty($_POST['id']) &&		isset($_POST['op']) && !empty($_POST['op']))	{		$id = $_POST['id'];		$op = $_POST['op'];				/* Inclusion du fichier de connexion à la BD */		include_once "connect-bd.php";				if ($op == "suppuser") {				$user = new User($bdd);			$image = new Image($bdd);			$gallerie = new Gallerie($bdd);						$suppression = $user->deleteUser($id);				if ($suppression)			{				$suppression = $image->deleteUserImages($id);								if ($suppression) {										$suppression = $gallerie->deleteUserGalleries($id);										if ($suppression) {											$bdd = null;						$reponse->success = true;											} else {						$reponse->message = "gallerie";					}									} else {										$reponse->message = "image";				}							} else {							$reponse->message = "utilisateur";			}					} else if ($op == "suppimage") {						$image = new Image($bdd);						$suppression = $image->deleteImage($id);						if ($suppression) {								$bdd = null;				$reponse->success = true;							} 					} else {					$image = new Image($bdd);			$user = new User($bdd);						$data = $image->getImage($id);						$author = $user->getPseudo($data['id_user']);						if ($data['is_public_img'] == "true") {								$public = "Image publique";							} else {							$public = "Image privée";			}						$reponse->success = true;			$reponse->titre = $data['titre_img'];			$reponse->description = $data['description_img'];			$reponse->auteur = "AUTEUR : " . $author['pseudo_user'];			$reponse->theme = "THÈME : " . $data['theme_img'];			$reponse->confid = "CONFIDENTIALITÉ : " . $public;			$reponse->jaime = "NOMBRE DE J'AIME : " . $data['nombre_aime_img'];			$reponse->lien = "<a href=\"../" . $data['lien_img'] . "\" target=\"_blank\">http://picstore.16mb.com/" . $data['lien_img'] . "</a>";					}			}	else	{		$reponse->message = "données manquantes.";	}	echo json_encode($reponse);