<?php 	session_start();	header('Cache-Control: no-cache, must-revalidate');	header('Expires: Mon, 26 Jul 1997 05:00:00 GMT');	header('Content-type: application/json');	require_once "../model/Image.php";	$reponse = new stdClass();	$reponse->success = false;	$reponse->message = "";	if (isset($_POST['id_usr']) && !empty($_POST['id_usr']) &&		isset($_POST['id_img']) && !empty($_POST['id_img']) &&		isset($_POST['op']) && !empty($_POST['op']))	{		$id_usr = $_POST['id_usr'];		$id_img = $_POST['id_img'];		$operation = $_POST['op'];				/* Inclusion du fichier de connexion à la BD */		include_once "connect-bd.php";				if ($operation == "verif") {				$image = new Image($bdd);						$check = $image->estAime($id_usr, $id_img);						if ($check) {				$reponse->success = true;				$reponse->message = $check;			} else {				$reponse->success = false;			}									} else if ($operation == "like") {						$image = new Image($bdd);						$like = $image->aimer($id_usr, $id_img);						if ($like) {				$reponse->success = true;			} else {				$reponse->success = false;				$reponse->message = "Image déjà aimé ?";			}					} else {					$image = new Image($bdd);						$dislike = $image->pasAimer($id_usr, $id_img);						if ($dislike) {				$reponse->success = true;			} else {				$reponse->success = false;				$reponse->message = "Image jamais aimé ?";			}		}			}	else	{		$reponse->message = "données manquantes.";	}	echo json_encode($reponse);